import classes from "./Table.module.css";
import { useEffect } from "react";

import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore/lite";

const Table = () => {
  const firebaseConfig = {
    apiKey: "AIzaSyAK0gaHVTxDJ5SQ9UmMT9ILR4yj7f5UUD8",
    authDomain: "islemler-c05ec.firebaseapp.com",
    projectId: "islemler-c05ec",
    storageBucket: "islemler-c05ec.appspot.com",
    messagingSenderId: "892044131029",
    appId: "1:892044131029:web:fd2a395e56950e1c59805c",
  };

  const app = initializeApp(firebaseConfig);
  const db = getFirestore(app);

  //   const getData = (db) => {
  //     getDocs(collection(db, "islemler")).then((item) =>
  //       console.log(item.json())
  //     );
  //   };

  useEffect(() => {
    async function getData(db) {
      const querySnapshot = await getDocs(collection(db, "islemler"));
      querySnapshot.forEach((doc) => {
        console.log(doc.data());
      });
    }
  }, []);

  // async function post(db) {
  //   const docRef = await addDoc(collection(db, "users"), {
  //     first: "Alan",
  //     middle: "Mathison",
