import classes from "./Table.module.css";

const Table = () => {
  function fetchMeals() {
    fetch("https://what-a-recipe-book-default-rtdb.firebaseio.com/meals.json")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const loadedMeals = [];

        for (const key in data) {
          loadedMeals.push({
            id: key,
            title: data[key].title,
            name: data[key].name,
            ingredients: data[key].ingredients,
            description: data[key].description,
            image: data[key].image,
            category: data[key].category,
          });
        }

        setMealList(loadedMeals);
      });
  }

  useEffect(() => {
    fetchMeals();
    return () => {};
  }, []);

  return (
    <div className={classes.container}>
      <table>
        <thead>
          <tr>
            <th>PFM TRX CODE</th>
            <th>CATEGORY</th>
            <th>TRX DEFINITION</th>
            <th>TRX DESCRIPTION</th>
            <th>SOURCE TRX CODE</th>
            <th>SOURCE OWNER</th>
            <th>BUSINESS OWNER</th>
            <th>TRX CREATED</th>
            <th>TRX UPDATE</th>
            <th>TRX CREATED DATE</th>
            <th>TRX UPDATE DATE</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td className={classes.check}>
              PTRX_001
              <input type="checkbox" />
            </td>
            <td>PARA GÖNDERME</td>
            <td>ACCOUNT SEND</td>
            <td>HESAPTAN KENDİ HESABINA PARA GÖNDERME</td>
            <td>ACC_001</td>
            <td>ACCOUNT-CUSTOMER</td>
            <td>CORE BANK</td>
            <td>PFM_SYS</td>
            <td>PFM_SYS</td>
            <td>01.01.2021</td>
            <td>01.01.2021</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default Table;
