import classes from "./Table.module.css";
import { useEffect, useState } from "react";

import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from "firebase/firestore/lite";

const Table = () => {
  const [data, setData] = useState([]);

  const firebaseConfig = {
    apiKey: "AIzaSyAK0gaHVTxDJ5SQ9UmMT9ILR4yj7f5UUD8",
    authDomain: "islemler-c05ec.firebaseapp.com",
    projectId: "islemler-c05ec",
    storageBucket: "islemler-c05ec.appspot.com",
    messagingSenderId: "892044131029",
    appId: "1:892044131029:web:fd2a395e56950e1c59805c",
  };

  const app = initializeApp(firebaseConfig);
  const db = getFirestore(app);

  useEffect(() => {
    async function getData(db) {
      const dataCol = collection(db, "islemler");
      const querySnapshot = await getDocs(dataCol);

      querySnapshot.forEach((doc) => {
        setData(doc.data());
      });
    }
    getData(db);
  }, []);

  console.log([data]);

  return (
    <div className={classes.container}>
      <table>
        <thead>
          <tr>
            <th>PFM TRX CODE</th>
            <th>CATEGORY</th>
            <th>TRX DEFINITION</th>
            <th>TRX DESCRIPTION</th>
            <th>SOURCE TRX CODE</th>
            <th>SOURCE OWNER</th>
            <th>BUSINESS OWNER</th>
            <th>TRX CREATED</th>
            <th>TRX UPDATE</th>
            <th>TRX CREATED DATE</th>
            <th>TRX UPDATE DATE</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td className={classes.check}>
              PTRX_001
              <input type="checkbox" />
            </td>
            <td>PARA GÖNDERME</td>
            <td>ACCOUNT SEND</td>
            <td>HESAPTAN KENDİ HESABINA PARA GÖNDERME</td>
            <td>ACC_001</td>
            <td>ACCOUNT-CUSTOMER</td>
            <td>CORE BANK</td>
            <td>PFM_SYS</td>
            <td>PFM_SYS</td>
            <td>01.01.2021</td>
            <td>01.01.2021</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default Table;
